CREATE TABLE city (id SMALLINT UNSIGNED AUTO_INCREMENT, state_id SMALLINT UNSIGNED DEFAULT 0 NOT NULL, name VARCHAR(150) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE event (id INT UNSIGNED AUTO_INCREMENT, name VARCHAR(150) NOT NULL, description text, start DATETIME, end DATETIME, state_id SMALLINT UNSIGNED DEFAULT 0 NOT NULL, city_id SMALLINT UNSIGNED DEFAULT 0 NOT NULL, address text, prepay_slots SMALLINT UNSIGNED DEFAULT 0 NOT NULL, max_capacity SMALLINT UNSIGNED DEFAULT 0 NOT NULL, admission_prepay DECIMAL(18, 2) DEFAULT 0 NOT NULL, admission_at_door DECIMAL(18, 2) DEFAULT 0 NOT NULL, admission_no_rsvp DECIMAL(18, 2) DEFAULT 0 NOT NULL, image_full VARCHAR(150) NOT NULL, image_small VARCHAR(150) NOT NULL, event_admin1 INT UNSIGNED DEFAULT 0 NOT NULL, event_admin2 INT UNSIGNED DEFAULT 0 NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE event_attendee (id INT UNSIGNED AUTO_INCREMENT, event_id INT UNSIGNED DEFAULT 0 NOT NULL, user_id INT UNSIGNED DEFAULT 0 NOT NULL, industry_id INT UNSIGNED DEFAULT 0 NOT NULL, paid ENUM('0', '1') DEFAULT '0' NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE industry (id INT UNSIGNED AUTO_INCREMENT, name VARCHAR(150) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE news (id SMALLINT UNSIGNED AUTO_INCREMENT, title VARCHAR(150) NOT NULL, content text, image_full VARCHAR(150) NOT NULL, image_small VARCHAR(150) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE profile (id INT UNSIGNED, title VARCHAR(5) NOT NULL, company VARCHAR(150) NOT NULL, state_id SMALLINT UNSIGNED DEFAULT 0 NOT NULL, city_id SMALLINT UNSIGNED DEFAULT 0 NOT NULL, address VARCHAR(255) NOT NULL, i_do text, to_meet text, image_full VARCHAR(150) NOT NULL, image_small VARCHAR(150) NOT NULL, linkedin_url VARCHAR(150) NOT NULL, fb_url VARCHAR(150) NOT NULL, twitter_url VARCHAR(150) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE profile_industry (id INT UNSIGNED AUTO_INCREMENT, user_id INT UNSIGNED DEFAULT 0 NOT NULL, industry_id INT UNSIGNED DEFAULT 0 NOT NULL, type_id ENUM('0', '1') DEFAULT '0' NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sponsor (id SMALLINT UNSIGNED AUTO_INCREMENT, position VARCHAR(1) NOT NULL, company VARCHAR(150) NOT NULL, image_full VARCHAR(150) NOT NULL, image_small VARCHAR(150) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE state (id SMALLINT UNSIGNED AUTO_INCREMENT, code VARCHAR(2) NOT NULL, name VARCHAR(150) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE user (id INT UNSIGNED AUTO_INCREMENT, email VARCHAR(150) NOT NULL UNIQUE, pass VARCHAR(32) NOT NULL, fname VARCHAR(150) NOT NULL, lname VARCHAR(150) NOT NULL, dob DATETIME, type_id ENUM('0', '1') DEFAULT '0' NOT NULL, app_type TINYINT UNSIGNED DEFAULT 0 NOT NULL, app_id VARCHAR(150) NOT NULL, activation VARCHAR(150) NOT NULL, last_login_at DATETIME, active ENUM('0', '1') DEFAULT '0' NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
